import React, { useEffect, useState } from 'react'
import EmployeeService from '../services/EmployeeService';
import Employee from './Employee';

const Employees = () => {

    const [employees, setEmployees] = useState(null);

    useEffect(() => {
      const fetchData = async () => {
        setLoading(true);
        try {
            const response = await EmployeeService.getEmployees();
            setEmployees(response.data);
        } catch (error) {
            console.log(error);
        }
        setLoading(false);
      };
      fetchData();
    }, []);
    

  return (
    <div class="flex flex-col justify-center items-center font-sans">
      <div className="px-3 py-6 text-center bg-green-600 tracking-tighter text-white font-thin w-full text-2xl">
        Employee List
      </div>
      <table class="table-auto">
        <thead class="text-white text-2xl shadow-lg bg-green-600">
          <tr class="border-slate-400">
            <th class="font-normal">ID</th>
            <th class="font-normal">Name</th>
            <th class="font-normal">Designation</th>
            <th class="font-normal">Salary</th>
          </tr>
        </thead>
        <tbody class="text-lg text-white bg-slate-400">
            {employees.map((employee) => (<Employee employee={employee} key={employee.id}></Employee>))}
        </tbody>
      </table>
    </div>
  );
}

export default Employees;
